import { Switch, Route, Link } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider, useAuth } from "@/contexts/auth-context";
import { VaultProvider } from "@/contexts/vault-context";
import NotFound from "@/pages/not-found";
import Login from "@/pages/login";
import Dashboard from "@/pages/dashboard";
import Passwords from "@/pages/passwords";
import Subscriptions from "@/pages/subscriptions";
import Notes from "@/pages/notes";
import Expenses from "@/pages/expenses";
import Reminders from "@/pages/reminders";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Lock, Search, RefreshCw, Settings, Bookmark, Key, BarChart3, Upload, Download, BookOpen, DollarSign, Bell } from "lucide-react";
import { useState } from "react";
import { PasswordGeneratorModal } from "@/components/password-generator-modal";
import { ImportExportModal } from "@/components/import-export-modal";
import { ExtensionPairingModal } from "@/components/extension-pairing-modal";
import { SecuritySettingsModal } from "@/components/security-settings-modal";
import { PWAOfflineIndicator } from "@/components/pwa-offline-indicator";
import { PWAInstallPrompt } from "@/components/pwa-install-prompt";
import { useVault } from "@/contexts/vault-context";

// Main Layout Component for authenticated users
function MainLayout({ children }: { children: React.ReactNode }) {
  const { logout } = useAuth();
  const { searchQuery, setSearchQuery, stats } = useVault();
  const [showGenerator, setShowGenerator] = useState(false);
  const [showImportExport, setShowImportExport] = useState(false);
  const [showExtensionPairing, setShowExtensionPairing] = useState(false);
  const [showSecuritySettings, setShowSecuritySettings] = useState(false);

  const handleLockVault = () => {
    logout();
  };

  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: BarChart3, count: null },
    { id: 'passwords', label: 'Passwords', icon: Key, count: stats.totalPasswords },
    { id: 'subscriptions', label: 'Subscriptions', icon: Bookmark, count: stats.activeSubscriptions },
    { id: 'notes', label: 'Notes', icon: BookOpen, count: stats.totalNotes },
    { id: 'expenses', label: 'Expenses', icon: DollarSign, count: stats.totalExpenses },
    { id: 'reminders', label: 'Reminders', icon: Bell, count: stats.totalReminders },
  ];

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card border-b border-border px-4 lg:px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
              <Lock className="w-5 h-5 text-primary-foreground" />
            </div>
            <h1 className="text-xl font-semibold text-foreground">SecureVault</h1>
          </div>

          <div className="flex items-center gap-3">
            {/* Search */}
            <div className="hidden sm:flex relative">
              <Input
                type="text"
                placeholder="Search passwords, subscriptions, notes..."
                className="w-64 pl-10 pr-4 py-2"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                data-testid="global-search"
              />
              <Search className="w-4 h-4 text-muted-foreground absolute left-3 top-1/2 transform -translate-y-1/2" />
            </div>

            {/* Actions */}
            <Button
              variant="ghost"
              size="sm"
              className="p-2 hover:bg-secondary rounded-lg"
              data-testid="sync-button"
            >
              <RefreshCw className="w-5 h-5" />
            </Button>

            <SecuritySettingsModal
              trigger={
                <Button
                  variant="ghost"
                  size="sm"
                  className="p-2 hover:bg-secondary rounded-lg"
                  data-testid="settings-button"
                >
                  <Settings className="w-5 h-5" />
                </Button>
              }
              onSettingsChanged={(kdfConfig) => {
                console.log('Security settings updated:', kdfConfig);
              }}
            />

            <Button
              variant="ghost"
              size="sm"
              onClick={handleLockVault}
              className="p-2 hover:bg-secondary rounded-lg"
              data-testid="lock-vault-button"
            >
              <Lock className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </header>

      <div className="flex h-[calc(100vh-73px)]">
        {/* Sidebar */}
        <nav className="w-64 bg-card border-r border-border p-4 space-y-2">
          <div className="space-y-1">
            {navItems.map((item) => (
              <Link key={item.id} href={item.id === 'dashboard' ? '/' : `/${item.id}`}>
                <Button
                  variant="ghost"
                  className={`w-full justify-start gap-3 px-3 py-2 hover:bg-secondary text-foreground`}
                  data-testid={`nav-${item.id}`}
                >
                  <item.icon className="w-5 h-5" />
                  {item.label}
                  {item.count !== null && (
                    <span className="ml-auto bg-muted text-muted-foreground text-xs px-2 py-1 rounded-full">
                      {item.count}
                    </span>
                  )}
                </Button>
              </Link>
            ))}
          </div>

          <hr className="border-border" />

          <div className="space-y-1">
            <h3 className="text-xs font-medium text-muted-foreground uppercase tracking-wide px-3">
              Tools
            </h3>
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 px-3 py-2 hover:bg-secondary text-foreground"
              onClick={() => setShowGenerator(true)}
              data-testid="nav-password-generator"
            >
              <RefreshCw className="w-5 h-5" />
              Password Generator
            </Button>
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 px-3 py-2 hover:bg-secondary text-foreground"
              onClick={() => setShowImportExport(true)}
              data-testid="nav-import-export"
            >
              <Upload className="w-5 h-5" />
              Import / Export
            </Button>
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 px-3 py-2 hover:bg-secondary text-foreground"
              onClick={() => setShowExtensionPairing(true)}
              data-testid="nav-extension-pairing"
            >
              <Settings className="w-5 h-5" />
              Browser Extension
            </Button>
          </div>
        </nav>

        {/* Main Content */}
        <main className="flex-1 overflow-auto">
          <div className="p-6">
            {children}
          </div>
        </main>
      </div>

      <PasswordGeneratorModal
        open={showGenerator}
        onOpenChange={setShowGenerator}
      />
      <ImportExportModal
        open={showImportExport}
        onOpenChange={setShowImportExport}
      />
      <ExtensionPairingModal
        open={showExtensionPairing}
        onOpenChange={setShowExtensionPairing}
      />
    </div>
  );
}

// Router Component
function Router() {
  const { isUnlocked, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center">
          <div className="w-16 h-16 bg-primary rounded-2xl flex items-center justify-center mb-4 mx-auto">
            <Lock className="w-8 h-8 text-primary-foreground" />
          </div>
          <p className="text-muted-foreground">Loading SecureVault...</p>
        </div>
      </div>
    );
  }

  return (
    <Switch>
      {!isUnlocked ? (
        <Route path="/" component={Login} />
      ) : (
        <>
          <Route path="/" component={() => (
            <MainLayout>
              <Dashboard />
            </MainLayout>
          )} />
          <Route path="/passwords" component={() => (
            <MainLayout>
              <Passwords />
            </MainLayout>
          )} />
          <Route path="/subscriptions" component={() => (
            <MainLayout>
              <Subscriptions />
            </MainLayout>
          )} />
          <Route path="/notes" component={() => (
            <MainLayout>
              <Notes />
            </MainLayout>
          )} />
          <Route path="/expenses" component={() => (
            <MainLayout>
              <Expenses />
            </MainLayout>
          )} />
          <Route path="/reminders" component={() => (
            <MainLayout>
              <Reminders />
            </MainLayout>
          )} />
        </>
      )}
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <AuthProvider>
          <VaultProvider>
            <Toaster />
            <Router />
            {/* PWA Components */}
            <PWAOfflineIndicator />
            <PWAInstallPrompt />
          </VaultProvider>
        </AuthProvider>
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

