}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  .password-dots {
    font-family: monospace;
    letter-spacing: 2px;
  }

  .strength-meter {
    height: 4px;
    border-radius: 2px;
    transition: all 0.3s ease;
  }

  .service-logo {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    object-fit: cover;
  }
}

.gradient-bg {
  background: linear-gradient(135deg, hsl(210, 40%, 98%) 0%, hsl(220, 70%, 95%) 100%);
}

.dark .gradient-bg {
  background: linear-gradient(135deg, hsl(222, 84%, 5%) 0%, hsl(217, 33%, 18%) 100%);
}

/* PWA Mobile Optimizations */
@layer base {
  html {
    /* Prevent zoom on input focus on iOS */
    -webkit-text-size-adjust: 100%;
    text-size-adjust: 100%;

    /* Smooth scrolling for better UX */
    scroll-behavior: smooth;
  }

  body {
    /* Prevent overscroll bounce on iOS */
    overscroll-behavior: none;

    /* Optimize scrolling performance */
    -webkit-overflow-scrolling: touch;

    /* Prevent text selection on UI elements */
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
  }

  /* Allow text selection on content areas */
  input, textarea, [contenteditable="true"], .text-selectable {
    -webkit-user-select: text;
    -moz-user-select: text;
    user-select: text;
  }

  /* Optimize tap highlights */
  button, [role="button"], input, select, textarea {
    -webkit-tap-highlight-color: transparent;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
  }

  /* Prevent zoom on double tap for iOS */
  * {
    touch-action: manipulation;
  }
}

/* PWA Safe Areas for notched devices */
@layer utilities {
  .safe-top {
    padding-top: env(safe-area-inset-top);
  }

  .safe-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .safe-left {
    padding-left: env(safe-area-inset-left);
  }

  .safe-right {
    padding-right: env(safe-area-inset-right);
  }

  .safe-area {
    padding-top: env(safe-area-inset-top);
    padding-bottom: env(safe-area-inset-bottom);
    padding-left: env(safe-area-inset-left);
    padding-right: env(safe-area-inset-right);
  }
}

/* PWA Loading States */
@layer components {
  .pwa-loading {
    @apply animate-pulse bg-muted rounded;
  }

  .pwa-offline {
    @apply bg-orange-50 border-orange-200 dark:bg-orange-950 dark:border-orange-800;
  }

  .pwa-update-available {
    @apply bg-blue-50 border-blue-200 dark:bg-blue-950 dark:border-blue-800;
  }
}

/* Mobile-first responsive breakpoints optimization */
@layer utilities {
  /* Touch-friendly sizing */
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Mobile viewport optimization */
  .mobile-viewport {
    min-height: 100vh;
    min-height: 100dvh; /* Dynamic viewport height for mobile */
  }

  /* Prevent horizontal scroll on mobile */
  .mobile-container {
    max-width: 100vw;
    overflow-x: hidden;
  }
}

/* PWA Install animation */
@keyframes pwa-slide-up {
  from {
    transform: translateY(100%);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes pwa-fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.pwa-install-prompt {
  animation: pwa-slide-up 0.3s ease-out;
}

.pwa-offline-indicator {
  animation: pwa-fade-in 0.2s ease-out;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --border: 2px;
    --radius: 4px;
  }

  button, input, select, textarea {
    border-width: 2px;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  .pwa-install-prompt,
  .pwa-offline-indicator {
    animation: none;
  }
}

