import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Eye, EyeOff, Wand2 } from 'lucide-react';
import { PASSWORD_CATEGORIES } from '@shared/schema';
import { useVault } from '@/contexts/vault-context';
import { useToast } from '@/hooks/use-toast';
import { PasswordStrengthMeter } from '@/components/ui/password-strength-meter';
import { PasswordGeneratorModal } from './password-generator-modal';

interface AddPasswordModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function AddPasswordModal({ open, onOpenChange }: AddPasswordModalProps) {
  const { addPassword } = useVault();
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    name: '',
    url: '',
    username: '',
    password: '',
    category: '',
    notes: '',
  });

  const [showPassword, setShowPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showGenerator, setShowGenerator] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name || !formData.username || !formData.password) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);
    try {
      await addPassword(formData);
      toast({
        title: "Success",
        description: "Password saved successfully",
      });

      // Reset form
      setFormData({
        name: '',
        url: '',
        username: '',
        password: '',
        category: '',
        notes: '',
      });
      onOpenChange(false);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to save password",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleGeneratedPassword = (password: string) => {
    setFormData(prev => ({ ...prev, password }));
  };

  return (
    <>
      <Dialog open={open} onOpenChange={onOpenChange}>
        <DialogContent className="sm:max-w-lg" data-testid="add-password-modal">
          <DialogHeader>
            <DialogTitle>Add New Password</DialogTitle>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="site-name">Site/Service Name *</Label>
              <Input
                id="site-name"
                placeholder="e.g., Google, Facebook, Banking"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                required
                data-testid="input-site-name"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="site-url">Website URL</Label>
              <Input
                id="site-url"
                type="url"
                placeholder="https://example.com"
                value={formData.url}
                onChange={(e) => setFormData(prev => ({ ...prev, url: e.target.value }))}
                data-testid="input-site-url"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="username">Username/Email *</Label>
              <Input
                id="username"
                placeholder="your.email@example.com"
                value={formData.username}
                onChange={(e) => setFormData(prev => ({ ...prev, username: e.target.value }))}
                required
                data-testid="input-username"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="password">Password *</Label>
              <div className="flex gap-2">
                <div className="relative flex-1">
                  <Input
                    id="password"
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Enter or generate password"
                    value={formData.password}
                    onChange={(e) => setFormData(prev => ({ ...prev, password: e.target.value }))}
                    required
                    className="pr-10"
                    data-testid="input-password"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                    onClick={() => setShowPassword(!showPassword)}
                    data-testid="toggle-password-visibility"
                  >
                    {showPassword ? (
                      <EyeOff className="h-4 w-4 text-muted-foreground" />
                    ) : (
                      <Eye className="h-4 w-4 text-muted-foreground" />
                    )}
                  </Button>
                </div>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setShowGenerator(true)}
                  data-testid="generate-password-button"
                >
                  <Wand2 className="h-4 w-4 mr-2" />
                  Generate
                </Button>
              </div>

              {formData.password && (
                <PasswordStrengthMeter password={formData.password} />
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="category">Category</Label>
              <Select
                value={formData.category}
                onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
              >
                <SelectTrigger data-testid="select-category">
                  <SelectValue placeholder="Select Category" />
                </SelectTrigger>
                <SelectContent>
                  {PASSWORD_CATEGORIES.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="notes">Notes (Optional)</Label>
              <Textarea
                id="notes"
                placeholder="Any additional notes or security questions..."
                value={formData.notes}
                onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                rows={3}
                className="resize-none"
                data-testid="textarea-notes"
              />
            </div>

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                className="flex-1"
                data-testid="cancel-button"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1"
                disabled={isSubmitting}
                data-testid="save-password-button"
              >
                {isSubmitting ? "Saving..." : "Save Password"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      <PasswordGeneratorModal
        open={showGenerator}
        onOpenChange={setShowGenerator}
        onPasswordGenerated={handleGeneratedPassword}
      />
    </>
  );
}

client/src/components/add-subscription-m.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Calendar } from '@/components/ui/calendar';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { CalendarIcon } from 'lucide-react';
import { format } from 'date-fns';
import { SUBSCRIPTION_CATEGORIES } from '@shared/schema';
import { useVault } from '@/contexts/vault-context';
import { useToast } from '@/hooks/use-toast';
import { cn } from '@/lib/utils';

interface AddSubscriptionModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function AddSubscriptionModal({ open, onOpenChange }: AddSubscriptionModalProps) {
  const { addSubscription } = useVault();
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    name: '',
    plan: '',
    cost: '',
    currency: 'USD',
    billingCycle: 'monthly' as 'monthly' | 'yearly' | 'weekly' | 'daily',
    nextBillingDate: undefined as Date | undefined,
    reminderDays: '7',
    category: '',
    notes: '',
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name || !formData.cost || !formData.nextBillingDate) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    const cost = parseFloat(formData.cost);
    if (isNaN(cost) || cost <= 0) {
      toast({
        title: "Error",
        description: "Please enter a valid cost",
        variant: "destructive",
      });
      return;
    }

    const reminderDays = parseInt(formData.reminderDays);
    if (isNaN(reminderDays) || reminderDays < 0) {
      toast({
        title: "Error",
        description: "Please enter a valid reminder days",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);
    try {
      await addSubscription({
        name: formData.name,
        plan: formData.plan || undefined,
        cost,
        currency: formData.currency,
        billingCycle: formData.billingCycle,
        nextBillingDate: formData.nextBillingDate,
        reminderDays,
        category: formData.category || undefined,
        notes: formData.notes || undefined,
        isActive: true,
      });

      toast({
        title: "Success",
        description: "Subscription saved successfully",
      });

      // Reset form
      setFormData({
        name: '',
        plan: '',
        cost: '',
        currency: 'USD',
        billingCycle: 'monthly',
        nextBillingDate: undefined,
        reminderDays: '7',
        category: '',
        notes: '',
      });
      onOpenChange(false);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to save subscription",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-lg max-h-[90vh] overflow-y-auto" data-testid="add-subscription-modal">
        <DialogHeader>
          <DialogTitle>Add New Subscription</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="service-name">Service Name *</Label>
            <Input
              id="service-name"
              placeholder="e.g., Netflix, Spotify, Adobe"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              required
              data-testid="input-service-name"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="plan">Plan/Package Name</Label>
            <Input
              id="plan"
              placeholder="e.g., Premium, Pro, Basic"
              value={formData.plan}
              onChange={(e) => setFormData(prev => ({ ...prev, plan: e.target.value }))}
              data-testid="input-plan"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="cost">Cost *</Label>
              <Input
                id="cost"
                type="number"
                step="0.01"
                min="0"
                placeholder="9.99"
                value={formData.cost}
                onChange={(e) => setFormData(prev => ({ ...prev, cost: e.target.value }))}
                required
                data-testid="input-cost"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="currency">Currency</Label>
              <Select
                value={formData.currency}
                onValueChange={(value) => setFormData(prev => ({ ...prev, currency: value }))}
              >
                <SelectTrigger data-testid="select-currency">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="USD">USD</SelectItem>
                  <SelectItem value="EUR">EUR</SelectItem>
                  <SelectItem value="GBP">GBP</SelectItem>
                  <SelectItem value="CAD">CAD</SelectItem>
                  <SelectItem value="AUD">AUD</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="billing-cycle">Billing Cycle</Label>
            <Select
              value={formData.billingCycle}
              onValueChange={(value: 'monthly' | 'yearly' | 'weekly' | 'daily') => 
                setFormData(prev => ({ ...prev, billingCycle: value }))
              }
            >
              <SelectTrigger data-testid="select-billing-cycle">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="monthly">Monthly</SelectItem>
                <SelectItem value="yearly">Yearly</SelectItem>
                <SelectItem value="weekly">Weekly</SelectItem>
                <SelectItem value="daily">Daily</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label>Next Billing Date *</Label>
            <Popover>
              <PopoverTrigger asChild>
                <Button
                  variant="outline"
                  className={cn(
                    "w-full justify-start text-left font-normal",
                    !formData.nextBillingDate && "text-muted-foreground"
                  )}
                  data-testid="billing-date-trigger"
                >
                  <CalendarIcon className="mr-2 h-4 w-4" />
                  {formData.nextBillingDate ? (
                    format(formData.nextBillingDate, "PPP")
                  ) : (
                    "Pick a date"
                  )}
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-auto p-0" align="start">
                <Calendar
                  mode="single"
                  selected={formData.nextBillingDate}
                  onSelect={(date) => setFormData(prev => ({ ...prev, nextBillingDate: date }))}
                  initialFocus
                />
              </PopoverContent>
            </Popover>
          </div>

          <div className="space-y-2">
            <Label htmlFor="reminder-days">Reminder Days</Label>
            <Input
              id="reminder-days"
              type="number"
              min="0"
              placeholder="7"
              value={formData.reminderDays}
              onChange={(e) => setFormData(prev => ({ ...prev, reminderDays: e.target.value }))}
              data-testid="input-reminder-days"
            />
            <p className="text-xs text-muted-foreground">
              How many days before billing date to remind you
            </p>
          </div>

          <div className="space-y-2">
            <Label htmlFor="category">Category</Label>
            <Select
              value={formData.category}
              onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
            >
              <SelectTrigger data-testid="select-category">
                <SelectValue placeholder="Select Category" />
              </SelectTrigger>
              <SelectContent>
                {SUBSCRIPTION_CATEGORIES.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="notes">Notes (Optional)</Label>
            <Textarea
              id="notes"
              placeholder="Any additional notes..."
              value={formData.notes}
              onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
              rows={3}
              className="resize-none"
              data-testid="textarea-notes"
            />
          </div>

          <div className="flex gap-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              className="flex-1"
              data-testid="cancel-button"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              className="flex-1"
              disabled={isSubmitting}
              data-testid="save-subscription-button"
            >
              {isSubmitting ? "Saving..." : "Save Subscription"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

