import React, { createContext, useContext, useState, useEffect } from 'react';
import { vaultStorage } from '@/lib/storage';

interface AuthContextType {
  isUnlocked: boolean;
  vaultExists: boolean;
  login: (masterPassword: string) => Promise<boolean>;
  createVault: (masterPassword: string) => Promise<void>;
  logout: () => void;
  isLoading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [isUnlocked, setIsUnlocked] = useState(false);
  const [vaultExists, setVaultExists] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    initializeAuth();
  }, []);

  const initializeAuth = async () => {
    try {
      await vaultStorage.init();
      const exists = await vaultStorage.vaultExists();
      setVaultExists(exists);
    } catch (error) {
      console.error('Failed to initialize auth:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const login = async (masterPassword: string): Promise<boolean> => {
    try {
      const success = await vaultStorage.unlockVault(masterPassword);
      if (success) {
        setIsUnlocked(true);
      }
      return success;
    } catch (error) {
      console.error('Login failed:', error);
      return false;
    }
  };

  const createVault = async (masterPassword: string): Promise<void> => {
    try {
      await vaultStorage.createVault(masterPassword);
      setVaultExists(true);
      setIsUnlocked(true);
    } catch (error) {
      console.error('Failed to create vault:', error);
      throw error;
    }
  };

  const logout = () => {
    setIsUnlocked(false);
    // Clear encryption key from storage
    vaultStorage.setEncryptionKey(null as any);
  };

  const value: AuthContextType = {
    isUnlocked,
    vaultExists,
    login,
    createVault,
    logout,
    isLoading,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

