// Crypto utilities for client-side encryption using Web Crypto API

// KDF configuration types
export type KDFConfig = {
  algorithm: 'PBKDF2';
  iterations: number;
  hash: 'SHA-256' | 'SHA-512';
};

export class CryptoService {
  private static readonly ALGORITHM = 'AES-GCM';
  private static readonly KEY_LENGTH = 256;
  private static readonly IV_LENGTH = 12;
  private static readonly SALT_LENGTH = 16;

  // Key derivation configuration - can be customized per vault
  private static readonly DEFAULT_KDF_CONFIG: KDFConfig = {
    algorithm: 'PBKDF2',
    iterations: 600000, // Increased from 100k to 600k (OWASP 2023 recommendation)
    hash: 'SHA-256'
  };

  // Enhanced KDF configuration options
  public static readonly KDF_PRESETS: Record<string, KDFConfig> = {
    // Standard security for most users
    standard: { algorithm: 'PBKDF2', iterations: 600000, hash: 'SHA-256' },

    // High security for sensitive data
    high: { algorithm: 'PBKDF2', iterations: 1000000, hash: 'SHA-256' },

    // Maximum security (slower but most secure)
    maximum: { algorithm: 'PBKDF2', iterations: 2000000, hash: 'SHA-512' },

    // Fast option for development/testing
    fast: { algorithm: 'PBKDF2', iterations: 100000, hash: 'SHA-256' }
  };

  // Generate a random salt
  static generateSalt(): Uint8Array {
    return crypto.getRandomValues(new Uint8Array(this.SALT_LENGTH));
  }

  // Generate a random IV
  static generateIV(): Uint8Array {
    return crypto.getRandomValues(new Uint8Array(this.IV_LENGTH));
  }

  // Derive key from master password using configurable KDF settings
  static async deriveKey(
    masterPassword: string, 
    salt: Uint8Array,
    kdfConfig: KDFConfig = this.DEFAULT_KDF_CONFIG
  ): Promise<CryptoKey> {
    const encoder = new TextEncoder();
    const passwordBuffer = encoder.encode(masterPassword);

    const baseKey = await crypto.subtle.importKey(
      'raw',
      passwordBuffer,
      kdfConfig.algorithm,
      false,
      ['deriveKey']
    );

    return crypto.subtle.deriveKey(
      {
        name: kdfConfig.algorithm,
        salt: salt,
        iterations: kdfConfig.iterations,
        hash: kdfConfig.hash
      },
      baseKey,
      {
        name: this.ALGORITHM,
        length: this.KEY_LENGTH
      },
      false,
      ['encrypt', 'decrypt']
    );
  }

  // Encrypt data
  static async encrypt(data: string, key: CryptoKey): Promise<{ encrypted: ArrayBuffer; iv: Uint8Array }> {
    const encoder = new TextEncoder();
    const dataBuffer = encoder.encode(data);
    const iv = this.generateIV();

    const encrypted = await crypto.subtle.encrypt(
      {
        name: this.ALGORITHM,
        iv: iv
      },
      key,
      dataBuffer
    );

    return { encrypted, iv };
  }

  // Decrypt data
  static async decrypt(encryptedData: ArrayBuffer, key: CryptoKey, iv: Uint8Array): Promise<string> {
    const decrypted = await crypto.subtle.decrypt(
      {
        name: this.ALGORITHM,
        iv: iv
      },
      key,
      encryptedData
    );

    const decoder = new TextDecoder();
    return decoder.decode(decrypted);
  }

  // Convert ArrayBuffer to base64 string for storage
  static arrayBufferToBase64(buffer: ArrayBuffer): string {
    const bytes = new Uint8Array(buffer);
    let binary = '';
    for (let i = 0; i < bytes.byteLength; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return btoa(binary);
  }

  // Convert base64 string back to ArrayBuffer
  static base64ToArrayBuffer(base64: string): ArrayBuffer {
    const binary = atob(base64);
    const bytes = new Uint8Array(binary.length);
    for (let i = 0; i < binary.length; i++) {
      bytes[i] = binary.charCodeAt(i);
    }
    return bytes.buffer;
  }

  // Convert Uint8Array to base64 string
  static uint8ArrayToBase64(uint8Array: Uint8Array): string {
    let binary = '';
    for (let i = 0; i < uint8Array.byteLength; i++) {
      binary += String.fromCharCode(uint8Array[i]);
    }
    return btoa(binary);
  }

  // Convert base64 string to Uint8Array
  static base64ToUint8Array(base64: string): Uint8Array {
    const binary = atob(base64);
    const bytes = new Uint8Array(binary.length);
    for (let i = 0; i < binary.length; i++) {
      bytes[i] = binary.charCodeAt(i);
    }
    return bytes;
  }

  // Benchmark KDF performance for different settings
  static async benchmarkKDF(
    testPassword: string = 'test-password-123',
    testSalt?: Uint8Array
  ): Promise<{ preset: string; config: any; timeMs: number }[]> {
    const salt = testSalt || this.generateSalt();
    const results: { preset: string; config: any; timeMs: number }[] = [];

    for (const [presetName, config] of Object.entries(this.KDF_PRESETS)) {
      const startTime = performance.now();

      try {
        await this.deriveKey(testPassword, salt, config);
        const endTime = performance.now();
        const timeMs = Math.round(endTime - startTime);

        results.push({
          preset: presetName,
          config: { ...config },
          timeMs
        });
      } catch (error) {
        console.error(`KDF benchmark failed for preset ${presetName}:`, error);
        results.push({
          preset: presetName,
          config: { ...config },
          timeMs: -1 // Error indicator
        });
      }
    }

    return results.sort((a, b) => a.timeMs - b.timeMs);
  }

  // Test password strength and recommend appropriate KDF preset
  static recommendKDFPreset(password: string): {
    preset: keyof typeof CryptoService.KDF_PRESETS;
    reason: string;
    config: typeof CryptoService.KDF_PRESETS[keyof typeof CryptoService.KDF_PRESETS];
  } {
    const length = password.length;
    const hasUpper = /[A-Z]/.test(password);
    const hasLower = /[a-z]/.test(password);
    const hasNumbers = /\d/.test(password);
    const hasSymbols = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    const complexityScore = [hasUpper, hasLower, hasNumbers, hasSymbols].filter(Boolean).length;

    // Strong password (long + complex) = standard KDF is sufficient
    if (length >= 16 && complexityScore >= 3) {
      return {
        preset: 'standard',
        reason: 'Strong password detected - standard security level recommended',
        config: this.KDF_PRESETS.standard
      };
    }

    // Medium password = high security KDF
    if (length >= 12 && complexityScore >= 2) {
      return {
        preset: 'high',
        reason: 'Medium strength password - higher security KDF recommended',
        config: this.KDF_PRESETS.high
      };
    }

    // Weak password = maximum security KDF
    return {
      preset: 'maximum',
      reason: 'Weak password detected - maximum security KDF strongly recommended',
      config: this.KDF_PRESETS.maximum
    };
  }

  // Enhanced key derivation with automatic timing and progress callback
  static async deriveKeyWithProgress(
    masterPassword: string,
    salt: Uint8Array,
    kdfConfig: KDFConfig = this.DEFAULT_KDF_CONFIG,
    onProgress?: (progress: number) => void
  ): Promise<{ key: CryptoKey; timeMs: number }> {
    const startTime = performance.now();

    // Simulate progress updates for long-running operations
    let progressInterval: NodeJS.Timeout | undefined;
    if (onProgress && kdfConfig.iterations > 500000) {
      let progress = 0;
      progressInterval = setInterval(() => {
        progress = Math.min(progress + Math.random() * 15, 95);
        onProgress(progress);
      }, 100);
    }

    try {
      const key = await this.deriveKey(masterPassword, salt, kdfConfig);
      const endTime = performance.now();
      const timeMs = Math.round(endTime - startTime);

      if (progressInterval) {
        clearInterval(progressInterval);
        onProgress?.(100);
      }

      return { key, timeMs };
    } catch (error) {
      if (progressInterval) {
        clearInterval(progressInterval);
      }
      throw error;
    }
  }
}

