import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Lock, Bookmark, DollarSign, AlertTriangle, Eye, Copy, Edit } from 'lucide-react';
import { useVault } from '@/contexts/vault-context';
import { format, isWithinInterval, addDays, startOfDay, differenceInCalendarDays } from 'date-fns';
import { useState } from 'react';
import { useToast } from '@/hooks/use-toast';

export default function Dashboard() {
  const { passwords, subscriptions, stats } = useVault();
  const { toast } = useToast();
  const [copiedId, setCopiedId] = useState<string | null>(null);

  // Get recent passwords (last 5)
  const recentPasswords = [...passwords]
    .sort((a, b) => new Date(b.lastUsed || b.updatedAt).getTime() - new Date(a.lastUsed || a.updatedAt).getTime())
    .slice(0, 5);

  // Get upcoming subscription renewals
  const upcomingRenewals = subscriptions
    .filter(s => s.isActive)
    .filter(s => {
      const today = new Date();
      const reminderDate = addDays(today, s.reminderDays);
      return s.nextBillingDate <= reminderDate;
    })
    .sort((a, b) => a.nextBillingDate.getTime() - b.nextBillingDate.getTime())
    .slice(0, 5);

  const copyPassword = async (password: string, id: string) => {
    try {
      await navigator.clipboard.writeText(password);
      setCopiedId(id);
      toast({
        title: "Copied",
        description: "Password copied to clipboard",
      });
      setTimeout(() => setCopiedId(null), 2000);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy password",
        variant: "destructive",
      });
    }
  };

  const getRenewalStatus = (nextBillingDate: Date, reminderDays: number) => {
    const today = new Date();
    const daysUntil = differenceInCalendarDays(nextBillingDate, today);

    // Overdue or due today
    if (daysUntil <= 0) {
      return { 
        text: daysUntil === 0 ? 'Due Today' : 'Overdue', 
        color: 'bg-red-100 text-red-700', 
        urgent: true 
      };
    }
    // Due tomorrow
    else if (daysUntil === 1) {
      return { 
        text: 'Due Tomorrow', 
        color: 'bg-red-100 text-red-700', 
        urgent: true 
      };
    }
    // Within reminder period
    else if (daysUntil <= reminderDays) {
      return { 
        text: `Due in ${daysUntil} days`, 
        color: 'bg-amber-100 text-amber-700', 
        urgent: true 
      };
    }
    // Outside reminder period
    else {
      return { 
        text: `Due in ${daysUntil} days`, 
        color: 'bg-blue-100 text-blue-700', 
        urgent: false 
      };
    }
  };

  return (
    <div className="p-6 space-y-8" data-testid="dashboard-page">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-blue-500/10 rounded-lg flex items-center justify-center">
                <Lock className="w-6 h-6 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Total Passwords</p>
                <p className="text-2xl font-bold text-foreground" data-testid="stat-total-passwords">
                  {stats.totalPasswords}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-purple-500/10 rounded-lg flex items-center justify-center">
                <Bookmark className="w-6 h-6 text-purple-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Active Subscriptions</p>
                <p className="text-2xl font-bold text-foreground" data-testid="stat-active-subscriptions">
                  {stats.activeSubscriptions}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-green-500/10 rounded-lg flex items-center justify-center">
                <DollarSign className="w-6 h-6 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Monthly Spend</p>
                <p className="text-2xl font-bold text-foreground" data-testid="stat-monthly-spend">
                  ${stats.monthlySpend.toFixed(2)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-amber-500/10 rounded-lg flex items-center justify-center">
                <AlertTriangle className="w-6 h-6 text-amber-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Weak Passwords</p>
                <p className="text-2xl font-bold text-foreground" data-testid="stat-weak-passwords">
                  {stats.weakPasswords}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity & Alerts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Passwords */}
        <Card>
          <CardHeader className="pb-3">
            <div className="flex items-center justify-between">
              <CardTitle className="text-lg">Recent Passwords</CardTitle>
              <Button variant="link" className="text-primary hover:underline text-sm p-0" data-testid="view-all-passwords">
                View all
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {recentPasswords.length === 0 ? (
              <p className="text-muted-foreground text-center py-8" data-testid="no-recent-passwords">
                No passwords yet. Add your first password to get started.
              </p>
            ) : (
              <div className="space-y-3">
                {recentPasswords.map((password) => (
                  <div
                    key={password.id}
                    className="flex items-center gap-3 p-3 hover:bg-secondary rounded-lg cursor-pointer group"
                    data-testid={`recent-password-${password.id}`}
                  >
                    <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center text-primary font-semibold text-sm">
                      {password.name.charAt(0).toUpperCase()}
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-foreground truncate">{password.name}</p>
                      <p className="text-sm text-muted-foreground truncate">{password.username}</p>
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {password.lastUsed 
                        ? format(new Date(password.lastUsed), 'MMM d') 
                        : format(new Date(password.updatedAt), 'MMM d')
                      }
                    </div>
                    <div className="opacity-0 group-hover:opacity-100 flex gap-1 transition-opacity">
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={(e) => {
                          e.stopPropagation();
                          copyPassword(password.password, password.id);
                        }}
                        data-testid={`copy-password-${password.id}`}
                      >
                        {copiedId === password.id ? (
                          <span className="text-green-600 text-xs">Copied!</span>
                        ) : (
                          <Copy className="h-4 w-4" />
                        )}
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Subscription Alerts */}
        <Card>
          <CardHeader className="pb-3">
            <div className="flex items-center justify-between">
              <CardTitle className="text-lg">Upcoming Renewals</CardTitle>
              <Button variant="link" className="text-primary hover:underline text-sm p-0" data-testid="view-all-renewals">
                View all
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {upcomingRenewals.length === 0 ? (
              <p className="text-muted-foreground text-center py-8" data-testid="no-upcoming-renewals">
                No upcoming renewals. All your subscriptions are up to date.
              </p>
            ) : (
              <div className="space-y-3">
                {upcomingRenewals.map((subscription) => {
                  const status = getRenewalStatus(subscription.nextBillingDate, subscription.reminderDays);
                  return (
                    <div
                      key={subscription.id}
                      className={`flex items-center gap-3 p-3 rounded-lg ${
                        status.urgent ? 'bg-red-50 border border-red-200' : 'hover:bg-secondary'
                      }`}
                      data-testid={`upcoming-subscription-${subscription.id}`}
                    >
                      <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center text-primary font-semibold text-sm">
                        {subscription.name.charAt(0).toUpperCase()}
                      </div>
                      <div className="flex-1">
                        <p className="font-medium text-foreground">
                          {subscription.name}
                          {subscription.plan && ` ${subscription.plan}`}
                        </p>
                        <p className="text-sm text-muted-foreground">
                          {format(subscription.nextBillingDate, 'MMM d, yyyy')} - 
                          ${subscription.cost.toFixed(2)}/{subscription.billingCycle.charAt(0)}
                        </p>
                      </div>
                      <div className={`px-2 py-1 rounded-full text-xs font-medium ${status.color}`}>
                        {status.text}
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Security Health */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Security Health</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center p-4 bg-red-50 rounded-lg">
              <div className="w-12 h-12 bg-red-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                <AlertTriangle className="w-6 h-6 text-red-600" />
              </div>
              <p className="text-2xl font-bold text-red-600">{stats.weakPasswords}</p>
              <p className="text-sm text-red-600">Weak Passwords</p>
            </div>

            <div className="text-center p-4 bg-amber-50 rounded-lg">
              <div className="w-12 h-12 bg-amber-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                <Copy className="w-6 h-6 text-amber-600" />
              </div>
              <p className="text-2xl font-bold text-amber-600">
                {passwords.filter(p => {
                  const duplicates = passwords.filter(other => other.password === p.password && other.id !== p.id);
                  return duplicates.length > 0;
                }).length}
              </p>
              <p className="text-sm text-amber-600">Reused Passwords</p>
            </div>

            <div className="text-center p-4 bg-green-50 rounded-lg">
              <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                <Lock className="w-6 h-6 text-green-600" />
              </div>
              <p className="text-2xl font-bold text-green-600">
                {stats.totalPasswords - stats.weakPasswords}
              </p>
              <p className="text-sm text-green-600">Strong Passwords</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { Eye, EyeOff, Lock, Fingerprint } from 'lucide-react';
import { useAuth } from '@/contexts/auth-context';
import { useToast } from '@/hooks/use-toast';

export default function Login() {
  const { login, createVault, vaultExists } = useAuth();
  const { toast } = useToast();

  const [masterPassword, setMasterPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [isCreatingVault, setIsCreatingVault] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!masterPassword) {
      toast({
        title: "Error",
        description: "Please enter your master password",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const success = await login(masterPassword);
      if (!success) {
        toast({
          title: "Authentication Failed",
          description: "Invalid master password. Please try again.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to unlock vault. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreateVault = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!masterPassword) {
      toast({
        title: "Error",
        description: "Please enter a master password",
        variant: "destructive",
      });
      return;
    }

    if (masterPassword.length < 8) {
      toast({
        title: "Error",
        description: "Master password must be at least 8 characters long",
        variant: "destructive",
      });
      return;
    }

    if (masterPassword !== confirmPassword) {
      toast({
        title: "Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      await createVault(masterPassword);
      toast({
        title: "Success",
        description: "Vault created successfully! Welcome to SecureVault.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create vault. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleBiometricAuth = async () => {
    // Web Authentication API for biometric authentication
    if (!navigator.credentials || !window.PublicKeyCredential) {
      toast({
        title: "Not Supported",
        description: "Biometric authentication is not supported on this device",
        variant: "destructive",
      });
      return;
    }

    toast({
      title: "Coming Soon",
      description: "Biometric authentication will be available in a future update",
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center gradient-bg px-4" data-testid="login-page">
      <div className="max-w-md w-full">
        {/* Logo and Title */}
        <div className="text-center mb-8">
          <div className="mx-auto w-16 h-16 bg-primary rounded-2xl flex items-center justify-center mb-4">
            <Lock className="w-8 h-8 text-primary-foreground" />
          </div>
          <h1 className="text-2xl font-bold text-foreground">SecureVault</h1>
          <p className="text-muted-foreground mt-2">Offline Password & Subscription Manager</p>
        </div>

        {/* Login/Create Vault Form */}
        <Card className="shadow-lg border">
          <CardContent className="pt-6">
            <form onSubmit={vaultExists ? handleLogin : handleCreateVault} className="space-y-4">
              <div>
                <Label htmlFor="masterPassword" className="block text-sm font-medium text-foreground mb-2">
                  {vaultExists ? 'Master Password' : 'Create Master Password'}
                </Label>
                <div className="relative">
                  <Input
                    type={showPassword ? 'text' : 'password'}
                    id="masterPassword"
                    className="pr-10"
                    placeholder={vaultExists ? 'Enter your master password' : 'Create a strong master password'}
                    value={masterPassword}
                    onChange={(e) => setMasterPassword(e.target.value)}
                    data-testid="input-master-password"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                    onClick={() => setShowPassword(!showPassword)}
                    data-testid="toggle-master-password"
                  >
                    {showPassword ? (
                      <EyeOff className="h-5 w-5 text-muted-foreground" />
                    ) : (
                      <Eye className="h-5 w-5 text-muted-foreground" />
                    )}
                  </Button>
                </div>
              </div>

              {!vaultExists && (
                <div>
                  <Label htmlFor="confirmPassword" className="block text-sm font-medium text-foreground mb-2">
                    Confirm Master Password
                  </Label>
                  <div className="relative">
                    <Input
                      type={showConfirmPassword ? 'text' : 'password'}
                      id="confirmPassword"
                      className="pr-10"
                      placeholder="Confirm your master password"
                      value={confirmPassword}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                      data-testid="input-confirm-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                      data-testid="toggle-confirm-password"
                    >
                      {showConfirmPassword ? (
                        <EyeOff className="h-5 w-5 text-muted-foreground" />
                      ) : (
                        <Eye className="h-5 w-5 text-muted-foreground" />
                      )}
                    </Button>
                  </div>
                </div>
              )}

              {vaultExists && (
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <Checkbox 
                      id="remember"
                      checked={rememberMe}
                      onCheckedChange={(checked) => setRememberMe(Boolean(checked))}
                      data-testid="checkbox-remember-me"
                    />
                    <Label htmlFor="remember" className="ml-2 text-sm text-muted-foreground">
                      Remember me
                    </Label>
                  </div>
                  <Button 
                    type="button" 
                    variant="link" 
                    className="text-sm text-primary hover:underline p-0"
                    data-testid="button-forgot-password"
                  >
                    Forgot password?
                  </Button>
                </div>
              )}

              <Button
                type="submit"
                className="w-full"
                disabled={isLoading}
                data-testid="button-unlock-vault"
              >
                {isLoading ? 'Processing...' : (vaultExists ? 'Unlock Vault' : 'Create Vault')}
              </Button>
            </form>

            {vaultExists && (
              <>
                {/* Biometric Option */}
                <div className="mt-4 pt-4 border-t border-border">
                  <Button
                    type="button"
                    variant="ghost"
                    className="w-full flex items-center justify-center gap-2 text-muted-foreground hover:text-foreground py-2"
                    onClick={handleBiometricAuth}
                    data-testid="button-biometric-auth"
                  >
                    <Fingerprint className="w-5 h-5" />
                    Use Biometric Authentication
                  </Button>
                </div>

                {/* Switch to Create Vault */}
                <div className="text-center mt-6">
                  <p className="text-sm text-muted-foreground">
                    Need to create a new vault?{' '}
                    <Button
                      type="button"
                      variant="link"
                      className="text-primary hover:underline p-0"
                      onClick={() => setIsCreatingVault(true)}
                      data-testid="button-create-new-vault"
                    >
                      Create New Vault
                    </Button>
                  </p>
                </div>
              </>
            )}

            {!vaultExists && (
              <div className="mt-4 p-4 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
                <p className="text-sm text-blue-700 dark:text-blue-300">
                  <strong>Important:</strong> Your master password cannot be recovered. 
                  Make sure to choose a strong password that you can remember.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

