client/src/pages/passwords.tsx.content:
import { useState, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Plus, Copy, Edit, Trash2, Eye, EyeOff, Search } from 'lucide-react';
import { useVault } from '@/contexts/vault-context';
import { useToast } from '@/hooks/use-toast';
import { PASSWORD_CATEGORIES } from '@shared/schema';
import { PasswordGenerator } from '@/lib/password-generator';
import { AddPasswordModal } from '@/components/add-password-modal';
import { format } from 'date-fns';

export default function Passwords() {
  const { passwords, deletePassword, searchQuery, setSearchQuery } = useVault();
  const { toast } = useToast();

  const [showAddModal, setShowAddModal] = useState(false);
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [strengthFilter, setStrengthFilter] = useState<string>('all');
  const [visiblePasswords, setVisiblePasswords] = useState<Set<string>>(new Set());
  const [copiedId, setCopiedId] = useState<string | null>(null);

  // Filter and search passwords
  const filteredPasswords = useMemo(() => {
    return passwords.filter(password => {
      // Search filter
      const matchesSearch = searchQuery === '' || 
        password.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        password.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
        password.url?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        password.category?.toLowerCase().includes(searchQuery.toLowerCase());

      // Category filter
      const matchesCategory = categoryFilter === 'all' || 
        password.category === categoryFilter;

      // Strength filter
      const { level } = PasswordGenerator.calculateStrength(password.password);
      const matchesStrength = strengthFilter === 'all' ||
        (strengthFilter === 'weak' && level === 'weak') ||
        (strengthFilter === 'medium' && level === 'medium') ||
        (strengthFilter === 'strong' && (level === 'strong' || level === 'very-strong'));

      return matchesSearch && matchesCategory && matchesStrength;
    });
  }, [passwords, searchQuery, categoryFilter, strengthFilter]);

  const togglePasswordVisibility = (id: string) => {
    setVisiblePasswords(prev => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  const copyPassword = async (password: string, id: string) => {
    try {
      await navigator.clipboard.writeText(password);
      setCopiedId(id);
      toast({
        title: "Copied",
        description: "Password copied to clipboard",
      });
      setTimeout(() => setCopiedId(null), 2000);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy password",
        variant: "destructive",
      });
    }
  };

  const copyUsername = async (username: string) => {
    try {
      await navigator.clipboard.writeText(username);
      toast({
        title: "Copied",
        description: "Username copied to clipboard",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy username",
        variant: "destructive",
      });
    }
  };

  const handleDeletePassword = async (id: string, name: string) => {
    if (confirm(`Are you sure you want to delete the password for "${name}"?`)) {
      try {
        await deletePassword(id);
        toast({
          title: "Deleted",
          description: "Password deleted successfully",
        });
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to delete password",
          variant: "destructive",
        });
      }
    }
  };

  const getStrengthIndicator = (password: string) => {
    const { level, score } = PasswordGenerator.calculateStrength(password);

    const colors = {
      weak: { bg: 'bg-red-500', text: 'text-red-600' },
      medium: { bg: 'bg-amber-500', text: 'text-amber-600' },
      strong: { bg: 'bg-blue-500', text: 'text-blue-600' },
      'very-strong': { bg: 'bg-green-500', text: 'text-green-600' },
    };

    const color = colors[level];
    const width = `${score}%`;

    return (
      <div className="flex items-center gap-2">
        <div className="w-16 h-2 bg-muted rounded-full overflow-hidden">
          <div 
            className={`h-full rounded-full transition-all duration-300 ${color.bg}`}
            style={{ width }}
          />
        </div>
        <span className={`text-xs font-medium capitalize ${color.text}`}>
          {level.replace('-', ' ')}
        </span>
      </div>
    );
  };

  return (
    <div className="p-6 space-y-6" data-testid="passwords-page">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-foreground">Password Vault</h1>
          <p className="text-muted-foreground">
            Manage your passwords securely with end-to-end encryption
          </p>
        </div>
        <Button
          onClick={() => setShowAddModal(true)}
          data-testid="add-password-button"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Password
        </Button>
      </div>

      {/* Search and Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="relative flex-1">
              <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground" />
              <Input
                placeholder="Search passwords & services..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="search-passwords"
              />
            </div>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-full sm:w-48" data-testid="filter-category">
                <SelectValue placeholder="All Categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {PASSWORD_CATEGORIES.map(category => (
                  <SelectItem key={category} value={category}>
                    {category}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Select value={strengthFilter} onValueChange={setStrengthFilter}>
              <SelectTrigger className="w-full sm:w-48" data-testid="filter-strength">
                <SelectValue placeholder="All Strength" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Strength</SelectItem>
                <SelectItem value="strong">Strong</SelectItem>
                <SelectItem value="medium">Medium</SelectItem>
                <SelectItem value="weak">Weak</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Passwords List */}
      {filteredPasswords.length === 0 ? (
        <Card>
          <CardContent className="p-12">
            <div className="text-center">
              <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                <Plus className="w-8 h-8 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-medium text-foreground mb-2">
                {passwords.length === 0 ? 'No passwords yet' : 'No passwords match your search'}
              </h3>
              <p className="text-muted-foreground mb-4">
                {passwords.length === 0 
                  ? 'Get started by adding your first password entry'
                  : 'Try adjusting your search terms or filters'
                }
              </p>
              {passwords.length === 0 && (
                <Button
                  onClick={() => setShowAddModal(true)}
                  data-testid="add-first-password-button"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add Your First Password
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      ) : (
        <Card>
          <CardHeader className="pb-0">
            <div className="flex items-center text-sm font-medium text-muted-foreground">
              <div className="flex-1">Site / Service</div>
              <div className="w-40 hidden sm:block">Username</div>
              <div className="w-32 hidden md:block">Strength</div>
              <div className="w-32 hidden lg:block">Last Used</div>
              <div className="w-32">Actions</div>
            </div>
          </CardHeader>
          <CardContent className="p-0">
            <div className="divide-y divide-border">
              {filteredPasswords.map((password) => (
                <div
                  key={password.id}
                  className="p-4 hover:bg-muted/50 transition-colors"
                  data-testid={`password-entry-${password.id}`}
                >
                  <div className="flex items-center gap-4">
                    <div className="flex-1 flex items-center gap-3">
                      <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center text-primary font-semibold">
                        {password.name.charAt(0).toUpperCase()}
                      </div>
                      <div className="min-w-0 flex-1">
                        <p className="font-medium text-foreground truncate">
                          {password.name}
                        </p>
                        <p className="text-sm text-muted-foreground truncate">
                          {password.url || 'No URL provided'}
                        </p>
                        <div className="sm:hidden mt-1">
                          <p className="text-xs text-muted-foreground truncate">
                            {password.username}
                          </p>
                        </div>
                      </div>
                    </div>

                    <div className="w-40 hidden sm:block">
                      <p className="text-sm text-foreground truncate">
                        {password.username}
                      </p>
                    </div>

                    <div className="w-32 hidden md:block">
                      {getStrengthIndicator(password.password)}
                    </div>

                    <div className="w-32 hidden lg:block">
                      <p className="text-sm text-muted-foreground">
                        {password.lastUsed 
                          ? format(new Date(password.lastUsed), 'MMM d')
                          : format(new Date(password.updatedAt), 'MMM d')
                        }
                      </p>
                    </div>

                    <div className="w-32 flex items-center gap-1">
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => togglePasswordVisibility(password.id)}
                        data-testid={`toggle-password-${password.id}`}
                      >
                        {visiblePasswords.has(password.id) ? (
                          <EyeOff className="h-4 w-4" />
                        ) : (
                          <Eye className="h-4 w-4" />
                        )}
                      </Button>

                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => copyUsername(password.username)}
                        title="Copy username"
                        data-testid={`copy-username-${password.id}`}
                      >
                        <Copy className="h-4 w-4" />
                      </Button>

                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => copyPassword(password.password, password.id)}
                        title="Copy password"
                        data-testid={`copy-password-${password.id}`}
                      >
                        {copiedId === password.id ? (
                          <span className="text-green-600 text-xs">âœ“</span>
                        ) : (
                          <Copy className="h-4 w-4" />
                        )}
                      </Button>

                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => handleDeletePassword(password.id, password.name)}
                        title="Delete password"
                        data-testid={`delete-password-${password.id}`}
                      >
                        <Trash2 className="h-4 w-4 text-destructive" />
                      </Button>
                    </div>
                  </div>

                  {visiblePasswords.has(password.id) && (
                    <div className="mt-3 p-3 bg-muted/50 rounded-lg">
                      <div className="flex items-center justify-between">
                        <code className="text-sm font-mono">
                          {password.password}
                        </code>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => copyPassword(password.password, password.id)}
                          data-testid={`copy-visible-password-${password.id}`}
                        >
                          <Copy className="h-3 w-3 mr-1" />
                          Copy
                        </Button>
                      </div>
                      {password.notes && (
                        <div className="mt-2 pt-2 border-t border-border">
                          <p className="text-xs text-muted-foreground">
                            <strong>Notes:</strong> {password.notes}
                          </p>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      <AddPasswordModal
        open={showAddModal}
        onOpenChange={setShowAddModal}
      />
    </div>
  );
}

