import { useState, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Plus, Edit, Trash2, Bell, Search, Calendar, DollarSign, BarChart3 } from 'lucide-react';
import { useVault } from '@/contexts/vault-context';
import { useToast } from '@/hooks/use-toast';
import { SUBSCRIPTION_CATEGORIES } from '@shared/schema';
import { AddSubscriptionModal } from '@/components/add-subscription-modal';
import { SubscriptionAnalytics } from '@/components/subscription-analytics';
import { format, addDays } from 'date-fns';

export default function Subscriptions() {
  const { subscriptions, deleteSubscription, searchQuery, setSearchQuery, stats } = useVault();
  const { toast } = useToast();

  const [showAddModal, setShowAddModal] = useState(false);
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  // Filter and search subscriptions
  const filteredSubscriptions = useMemo(() => {
    return subscriptions.filter(subscription => {
      // Search filter
      const matchesSearch = searchQuery === '' || 
        subscription.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (subscription.plan ?? '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        (subscription.category ?? '').toLowerCase().includes(searchQuery.toLowerCase());

      // Category filter
      const matchesCategory = categoryFilter === 'all' || 
        subscription.category === categoryFilter;

      // Status filter
      const matchesStatus = statusFilter === 'all' ||
        (statusFilter === 'active' && subscription.isActive) ||
        (statusFilter === 'inactive' && !subscription.isActive);

      return matchesSearch && matchesCategory && matchesStatus;
    });
  }, [subscriptions, searchQuery, categoryFilter, statusFilter]);

  const handleDeleteSubscription = async (id: string, name: string) => {
    if (confirm(`Are you sure you want to delete the subscription for "${name}"?`)) {
      try {
        await deleteSubscription(id);
        toast({
          title: "Deleted",
          description: "Subscription deleted successfully",
        });
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to delete subscription",
          variant: "destructive",
        });
      }
    }
  };

  const getRenewalStatus = (nextBillingDate: Date, reminderDays: number, isActive: boolean) => {
    if (!isActive) {
      return { text: 'Inactive', color: 'bg-gray-100 text-gray-700', urgent: false };
    }

    const today = new Date();
    const daysUntil = Math.ceil((nextBillingDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));

    // Overdue or due today
    if (daysUntil <= 0) {
      return { 
        text: daysUntil === 0 ? 'Due Today' : 'Overdue', 
        color: 'bg-red-100 text-red-700 border-red-200', 
        urgent: true 
      };
    }
    // Due tomorrow
    else if (daysUntil === 1) {
      return { 
        text: 'Due Tomorrow', 
        color: 'bg-red-100 text-red-700 border-red-200', 
        urgent: true 
      };
    }
    // Within reminder period
    else if (daysUntil <= reminderDays) {
      return { 
        text: `Due in ${daysUntil} days`, 
        color: 'bg-amber-100 text-amber-700 border-amber-200', 
        urgent: true 
      };
    }
    // Outside reminder period but within a week
    else if (daysUntil <= 7) {
      return { 
        text: `Due in ${daysUntil} days`, 
        color: 'bg-blue-100 text-blue-700 border-blue-200', 
        urgent: false 
      };
    } 
    // More than a week away
    else {
      return { 
        text: `Due in ${daysUntil} days`, 
        color: 'bg-green-100 text-green-700 border-green-200', 
        urgent: false 
      };
    }
  };

  const formatCurrency = (amount: number, currency: string) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const getBillingCycleShort = (cycle: string) => {
    switch (cycle) {
      case 'monthly': return '/mo';
      case 'yearly': return '/yr';
      case 'weekly': return '/wk';
      case 'daily': return '/day';
      default: return '';
    }
  };

  // Calculate spending summary for different periods
  const spendingSummary = useMemo(() => {
    const activeSubscriptions = subscriptions.filter(s => s.isActive);

    const monthlySpend = activeSubscriptions.reduce((total, s) => {
      let monthlyAmount = s.cost;
      if (s.billingCycle === 'yearly') monthlyAmount /= 12;
      else if (s.billingCycle === 'weekly') monthlyAmount *= 4.33;
      else if (s.billingCycle === 'daily') monthlyAmount *= 30;
      return total + monthlyAmount;
    }, 0);

    const yearlySpend = monthlySpend * 12;

    return {
      monthly: monthlySpend,
      yearly: yearlySpend,
      count: activeSubscriptions.length,
    };
  }, [subscriptions]);

  return (
    <div className="p-6 space-y-6" data-testid="subscriptions-page">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-foreground">Subscriptions</h1>
          <p className="text-muted-foreground">
            Track and manage your recurring subscriptions and spending
          </p>
        </div>
        <Button
          onClick={() => setShowAddModal(true)}
          data-testid="add-subscription-button"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Subscription
        </Button>
      </div>

      {/* Quick Spending Summary */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <DollarSign className="w-5 h-5" />
            Quick Overview
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="text-center">
              <p className="text-3xl font-bold text-primary" data-testid="monthly-spend">
                {formatCurrency(spendingSummary.monthly, 'USD')}
              </p>
              <p className="text-sm text-muted-foreground">Monthly (USD)</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-muted-foreground" data-testid="yearly-spend">
                {formatCurrency(spendingSummary.yearly, 'USD')}
              </p>
              <p className="text-sm text-muted-foreground">Yearly (USD)</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600" data-testid="active-count">
                {spendingSummary.count}
              </p>
              <p className="text-sm text-green-600">Active Subscriptions</p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Tabs for Subscriptions and Analytics */}
      <Tabs defaultValue="subscriptions" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="subscriptions" data-testid="subscriptions-tab">
            <Calendar className="w-4 h-4 mr-2" />
            Subscriptions
          </TabsTrigger>
          <TabsTrigger value="analytics" data-testid="analytics-tab">
            <BarChart3 className="w-4 h-4 mr-2" />
            Analytics
          </TabsTrigger>
        </TabsList>

        <TabsContent value="subscriptions" className="space-y-6">
          {/* Search and Filters */}
          <Card>
            <CardContent className="p-4">
              <div className="flex flex-col sm:flex-row gap-4">
                <div className="relative flex-1">
                  <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground" />
                  <Input
                    placeholder="Search subscriptions..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                    data-testid="search-subscriptions"
                  />
                </div>
                <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                  <SelectTrigger className="w-full sm:w-48" data-testid="filter-category">
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {SUBSCRIPTION_CATEGORIES.map(category => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger className="w-full sm:w-48" data-testid="filter-status">
                    <SelectValue placeholder="All Status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="inactive">Inactive</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </CardContent>
          </Card>

          {/* Subscriptions Grid */}
          {filteredSubscriptions.length === 0 ? (
            <Card>
              <CardContent className="p-12">
                <div className="text-center">
                  <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                    <Plus className="w-8 h-8 text-muted-foreground" />
                  </div>
                  <h3 className="text-lg font-medium text-foreground mb-2">
                    {subscriptions.length === 0 ? 'No subscriptions yet' : 'No subscriptions match your search'}
                  </h3>
                  <p className="text-muted-foreground mb-4">
                    {subscriptions.length === 0 
                      ? 'Start tracking your subscriptions to manage your spending'
                      : 'Try adjusting your search terms or filters'
                    }
                  </p>
                  {subscriptions.length === 0 && (
                    <Button
                      onClick={() => setShowAddModal(true)}
                      data-testid="add-first-subscription-button"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Add Your First Subscription
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredSubscriptions.map((subscription) => {
                const status = getRenewalStatus(subscription.nextBillingDate, subscription.reminderDays, subscription.isActive);

                return (
                  <Card
                    key={subscription.id}
                    className={`hover:shadow-md transition-shadow ${status.urgent ? 'ring-2 ring-red-200' : ''}`}
                    data-testid={`subscription-card-${subscription.id}`}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center gap-3 mb-4">
                        <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center text-primary font-semibold text-lg">
                          {subscription.name.charAt(0).toUpperCase()}
                        </div>
                        <div className="flex-1 min-w-0">
                          <h4 className="font-semibold text-foreground truncate">
                            {subscription.name}
                          </h4>
                          <p className="text-sm text-muted-foreground truncate">
                            {subscription.plan || 'Standard Plan'}
                          </p>
                        </div>
                      </div>

                      <div className="space-y-2 mb-4">
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-muted-foreground">Cost</span>
                          <span className="font-medium text-foreground">
                            {formatCurrency(subscription.cost, subscription.currency)}
                            {getBillingCycleShort(subscription.billingCycle)}
                          </span>
                        </div>

                        <div className="flex justify-between items-center">
                          <span className="text-sm text-muted-foreground">Next Billing</span>
                          <span className="font-medium text-foreground text-sm">
                            {format(subscription.nextBillingDate, 'MMM d, yyyy')}
                          </span>
                        </div>

                        <div className="flex justify-between items-center">
                          <span className="text-sm text-muted-foreground">Status</span>
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${status.color}`}>
                            {status.text}
                          </span>
                        </div>

                        {subscription.category && (
                          <div className="flex justify-between items-center">
                            <span className="text-sm text-muted-foreground">Category</span>
                            <span className="text-sm font-medium text-foreground">
                              {subscription.category}
                            </span>
                          </div>
                        )}
                      </div>

                      {subscription.notes && (
                        <div className="mb-4 p-3 bg-muted/50 rounded-lg">
                          <p className="text-xs text-muted-foreground">
                            <strong>Notes:</strong> {subscription.notes}
                          </p>
                        </div>
                      )}

                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          className="flex-1"
                          data-testid={`edit-subscription-${subscription.id}`}
                        >
                          <Edit className="w-3 w-3 mr-1" />
                          Edit
                        </Button>

                        <Button
                          variant="outline"
                          size="sm"
                          className="flex-1"
                          data-testid={`remind-subscription-${subscription.id}`}
                        >
                          <Bell className="w-3 w-3 mr-1" />
                          Remind
                        </Button>

                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDeleteSubscription(subscription.id, subscription.name)}
                          data-testid={`delete-subscription-${subscription.id}`}
                        >
                          <Trash2 className="w-3 w-3 text-destructive" />
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </TabsContent>

        <TabsContent value="analytics" className="space-y-6">
          <SubscriptionAnalytics 
            subscriptions={filteredSubscriptions} 
            allSubscriptions={subscriptions}
            searchQuery={searchQuery}
            categoryFilter={categoryFilter}
            statusFilter={statusFilter}
          />
        </TabsContent>
      </Tabs>

      <AddSubscriptionModal
        open={showAddModal}
        onOpenChange={setShowAddModal}
      />
    </div>
  );
}

