import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Download, Upload, FileText, Shield, Database } from 'lucide-react';
import { useVault } from '@/contexts/vault-context';
import { useToast } from '@/hooks/use-toast';

interface ImportExportModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function ImportExportModal({ open, onOpenChange }: ImportExportModalProps) {
  const { exportVault, importVault, importPasswordsFromCSV, getAvailableCSVParsers } = useVault();
  const { toast } = useToast();

  const [exportPassword, setExportPassword] = useState('');
  const [importPassword, setImportPassword] = useState('');
  const [importFile, setImportFile] = useState<File | null>(null);
  const [isExporting, setIsExporting] = useState(false);
  const [isImporting, setIsImporting] = useState(false);

  // CSV import state
  const [csvFile, setCsvFile] = useState<File | null>(null);
  const [selectedParser, setSelectedParser] = useState<string>('');
  const [isImportingCSV, setIsImportingCSV] = useState(false);
  const csvParsers = getAvailableCSVParsers();

  const handleExport = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!exportPassword.trim()) {
      toast({
        title: "Error",
        description: "Please enter a password for the export file",
        variant: "destructive",
      });
      return;
    }

    setIsExporting(true);
    try {
      const encryptedData = await exportVault(exportPassword);

      // Create and download the file
      const blob = new Blob([encryptedData], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `securevault-backup-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast({
        title: "Export Complete",
        description: "Your vault has been exported successfully",
      });

      setExportPassword('');
      onOpenChange(false);
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export vault data",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  const handleImport = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!importFile) {
      toast({
        title: "Error",
        description: "Please select a backup file to import",
        variant: "destructive",
      });
      return;
    }

    if (!importPassword.trim()) {
      toast({
        title: "Error",
        description: "Please enter the password for the backup file",
        variant: "destructive",
      });
      return;
    }

    setIsImporting(true);
    try {
      const fileContent = await importFile.text();
      await importVault(fileContent, importPassword);

      toast({
        title: "Import Complete",
        description: "Your vault data has been imported successfully",
      });

      setImportPassword('');
      setImportFile(null);
      onOpenChange(false);
    } catch (error) {
      toast({
        title: "Import Failed",
        description: "Failed to import vault data. Please check your password and file.",
        variant: "destructive",
      });
    } finally {
      setIsImporting(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.type !== 'application/json' && !file.name.endsWith('.json')) {
        toast({
          title: "Invalid File",
          description: "Please select a JSON backup file",
          variant: "destructive",
        });
        return;
      }
      setImportFile(file);
    }
  };

  const handleCSVFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (!file.name.endsWith('.csv') && file.type !== 'text/csv') {
        toast({
          title: "Invalid File",
          description: "Please select a CSV file",
          variant: "destructive",
        });
        return;
      }
      setCsvFile(file);
      // Auto-select generic parser if no parser is selected
      if (!selectedParser) {
        setSelectedParser('generic');
      }
    }
  };

  const handleCSVImport = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!csvFile) {
      toast({
        title: "Error",
        description: "Please select a CSV file to import",
        variant: "destructive",
      });
      return;
    }

    if (!selectedParser) {
      toast({
        title: "Error", 
        description: "Please select a password manager format",
        variant: "destructive",
      });
      return;
    }

    setIsImportingCSV(true);
    try {
      const csvContent = await csvFile.text();
      const result = await importPasswordsFromCSV(csvContent, selectedParser);

      toast({
        title: "CSV Import Complete",
        description: `Successfully imported ${result.imported} passwords. ${result.skipped} duplicates were skipped.`,
      });

      setCsvFile(null);
      setSelectedParser('');
      onOpenChange(false);
    } catch (error) {
      toast({
        title: "CSV Import Failed",
        description: error instanceof Error ? error.message : "Failed to import CSV file",
        variant: "destructive",
      });
    } finally {
      setIsImportingCSV(false);
    }
  };

  const resetForm = () => {
    setExportPassword('');
    setImportPassword('');
    setImportFile(null);
    setCsvFile(null);
    setSelectedParser('');
  };

  const handleOpenChange = (open: boolean) => {
    if (!open) {
      resetForm();
    }
    onOpenChange(open);
  };

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogContent className="sm:max-w-[500px]" data-testid="import-export-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileText className="w-5 h-5" />
            Import / Export Vault
          </DialogTitle>
        </DialogHeader>

        <Tabs defaultValue="export" className="w-full">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="export" data-testid="tab-export">Export</TabsTrigger>
            <TabsTrigger value="import" data-testid="tab-import">Import</TabsTrigger>
            <TabsTrigger value="csv-import" data-testid="tab-csv-import">CSV Import</TabsTrigger>
          </TabsList>

          <TabsContent value="export" className="space-y-4">
            <div className="rounded-lg bg-muted p-4">
              <div className="flex items-center gap-2 mb-2">
                <Shield className="w-4 h-4 text-primary" />
                <span className="text-sm font-medium">Export Security</span>
              </div>
              <p className="text-sm text-muted-foreground">
                Your vault data will be encrypted with the password you provide below. 
                Store this password safely - you'll need it to import the data later.
              </p>
            </div>

            <form onSubmit={handleExport} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="export-password">Export Password</Label>
                <Input
                  id="export-password"
                  type="password"
                  value={exportPassword}
                  onChange={(e) => setExportPassword(e.target.value)}
                  placeholder="Enter a secure password for the export file"
                  data-testid="input-export-password"
                  disabled={isExporting}
                />
              </div>

              <Button 
                type="submit" 
                disabled={isExporting || !exportPassword.trim()}
                className="w-full"
                data-testid="button-export"
              >
                {isExporting ? (
                  "Exporting..."
                ) : (
                  <>
                    <Download className="w-4 h-4 mr-2" />
                    Export Vault
                  </>
                )}
              </Button>
            </form>
          </TabsContent>

          <TabsContent value="import" className="space-y-4">
            <div className="rounded-lg bg-muted p-4">
              <div className="flex items-center gap-2 mb-2">
                <Shield className="w-4 h-4 text-primary" />
                <span className="text-sm font-medium">Import Security</span>
              </div>
              <p className="text-sm text-muted-foreground">
                Select your SecureVault backup file and enter the password used during export. 
                This will add the imported data to your current vault.
              </p>
            </div>

            <form onSubmit={handleImport} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="import-file">Backup File</Label>
                <Input
                  id="import-file"
                  type="file"
                  accept=".json,application/json"
                  onChange={handleFileChange}
                  data-testid="input-import-file"
                  disabled={isImporting}
                />
                {importFile && (
                  <p className="text-sm text-muted-foreground">
                    Selected: {importFile.name}
                  </p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="import-password">Import Password</Label>
                <Input
                  id="import-password"
                  type="password"
                  value={importPassword}
                  onChange={(e) => setImportPassword(e.target.value)}
                  placeholder="Enter the export password"
                  data-testid="input-import-password"
                  disabled={isImporting}
                />
              </div>

              <Button 
                type="submit" 
                disabled={isImporting || !importFile || !importPassword.trim()}
                className="w-full"
                data-testid="button-import"
              >
                {isImporting ? (
                  "Importing..."
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Import Vault
                  </>
                )}
              </Button>
            </form>
          </TabsContent>

          <TabsContent value="csv-import" className="space-y-4">
            <div className="rounded-lg bg-muted p-4">
              <div className="flex items-center gap-2 mb-2">
                <Database className="w-4 h-4 text-primary" />
                <span className="text-sm font-medium">CSV Import</span>
              </div>
              <p className="text-sm text-muted-foreground">
                Import passwords from popular password managers. Select your password manager format and CSV export file.
                Duplicates will be skipped automatically.
              </p>
            </div>

            <form onSubmit={handleCSVImport} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="parser-select">Password Manager Format</Label>
                <Select value={selectedParser} onValueChange={setSelectedParser} disabled={isImportingCSV}>
                  <SelectTrigger data-testid="select-csv-parser">
                    <SelectValue placeholder="Choose password manager format" />
                  </SelectTrigger>
                  <SelectContent>
                    {csvParsers.map((parser) => (
                      <SelectItem key={parser.id} value={parser.id}>
                        {parser.name} - {parser.description}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="csv-file">CSV File</Label>
                <Input
                  id="csv-file"
                  type="file"
                  accept=".csv,text/csv"
                  onChange={handleCSVFileChange}
                  data-testid="input-csv-file"
                  disabled={isImportingCSV}
                />
                {csvFile && (
                  <p className="text-sm text-muted-foreground">
                    Selected: {csvFile.name}
                  </p>
                )}
              </div>

              <Button 
                type="submit" 
                disabled={isImportingCSV || !csvFile || !selectedParser}
                className="w-full"
                data-testid="button-csv-import"
              >
                {isImportingCSV ? (
                  "Importing..."
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Import CSV
                  </>
                )}
              </Button>
            </form>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

