// SecureVault Extension Popup Script

document.addEventListener('DOMContentLoaded', async () => {
  // Initialize popup
  await initializePopup();

  // Set up event listeners
  setupEventListeners();

  // Check vault connection status
  await checkVaultStatus();

  // Update last sync time
  updateLastSyncTime();
});

// Initialize popup state
async function initializePopup() {
  // Load settings from storage
  const settings = await chrome.storage.local.get([
    'extensionEnabled',
    'autoFillEnabled', 
    'showHints',
    'lastSync'
  ]);

  // Update toggle states
  updateToggle('autoFillToggle', settings.autoFillEnabled !== false);
  updateToggle('hintsToggle', settings.showHints !== false);
}

// Set up event listeners
function setupEventListeners() {
  // Open vault button
  document.getElementById('openVaultBtn').addEventListener('click', openVault);

  // Auto-fill button
  document.getElementById('fillBtn').addEventListener('click', autoFillCurrentPage);

  // Sync button
  document.getElementById('syncBtn').addEventListener('click', syncWithVault);

  // Pair button
  document.getElementById('pairBtn').addEventListener('click', pairWithVault);

  // Settings toggles
  document.getElementById('autoFillToggle').addEventListener('click', toggleAutoFill);
  document.getElementById('hintsToggle').addEventListener('click', toggleHints);
}

// Check vault connection status
async function checkVaultStatus() {
  const statusDot = document.getElementById('statusDot');
  const statusText = document.getElementById('statusText');
  const statusDetail = document.getElementById('statusDetail');
  const fillBtn = document.getElementById('fillBtn');

  try {
    // Send message to background script to check vault status
    const response = await sendMessage({
      type: 'GET_VAULT_STATUS'
    });

    if (response.success && response.connected) {
      // Connected
      statusDot.className = 'status-dot status-connected';
      statusText.textContent = 'Connected to SecureVault';
      statusDetail.textContent = `Vault app: ${response.vaultUrl}`;
      fillBtn.disabled = false;
    } else {
      // Disconnected
      statusDot.className = 'status-dot status-disconnected';
      statusText.textContent = 'Cannot connect to SecureVault';
      statusDetail.textContent = 'Make sure SecureVault app is running';
      fillBtn.disabled = true;
    }
  } catch (error) {
    console.error('Status check failed:', error);
    statusDot.className = 'status-dot status-disconnected';
    statusText.textContent = 'Connection failed';
    statusDetail.textContent = error.message;
    fillBtn.disabled = true;
  }
}

// Open SecureVault app
async function openVault() {
  try {
    // Get vault URL from storage or use default
    const result = await chrome.storage.local.get(['vaultUrl']);
    const vaultUrl = result.vaultUrl || 'http://localhost:5000';

    // Open new tab with vault app
    await chrome.tabs.create({ url: vaultUrl });

    // Close popup
    window.close();
  } catch (error) {
    console.error('Failed to open vault:', error);
    showError('Failed to open SecureVault app');
  }
}

// Auto-fill current page
async function autoFillCurrentPage() {
  const fillBtn = document.getElementById('fillBtn');
  const originalText = fillBtn.innerHTML;

  try {
    // Show loading state
    fillBtn.innerHTML = `
      <div class="loading"></div>
      Filling...
    `;
    fillBtn.disabled = true;

    // Get current tab
    const [tab] = await chrome.tabs.query({ active: true, currentWindow: true });

    if (!tab) {
      throw new Error('No active tab found');
    }

    // Extract domain from tab URL
    const url = new URL(tab.url);
    const domain = url.hostname.replace(/^www\./, '');

    // Get passwords for domain
    const response = await sendMessage({
      type: 'GET_PASSWORDS_FOR_DOMAIN',
      domain: domain
    });

    if (response.success && response.passwords.length > 0) {
      // Send fill command to content script
      await chrome.tabs.sendMessage(tab.id, {
        type: 'FILL_CREDENTIALS',
        credentials: response.passwords[0] // Use first match for now
      });

      showSuccess('Credentials filled successfully!');
    } else {
      showError('No saved passwords found for this site');
    }

  } catch (error) {
    console.error('Auto-fill failed:', error);
    showError('Auto-fill failed. Please try again.');
  } finally {
    // Restore button state
    fillBtn.innerHTML = originalText;
    fillBtn.disabled = false;
  }
}

// Sync with vault
async function syncWithVault() {
  const syncBtn = document.getElementById('syncBtn');
  const syncText = document.getElementById('syncText');
  const originalText = syncText.textContent;

  try {
    // Show loading state
    syncText.textContent = 'Syncing...';
    syncBtn.disabled = true;

    // Send sync message to background
    const response = await sendMessage({
      type: 'SYNC_WITH_VAULT'
    });

    if (response.success) {
      showSuccess('Sync completed successfully');

      // Update last sync time
      await chrome.storage.local.set({
        lastSync: response.lastSync
      });

      updateLastSyncTime();
    } else {
      throw new Error(response.error || 'Sync failed');
    }

  } catch (error) {
    console.error('Sync failed:', error);
    showError('Sync failed. Check your connection to SecureVault.');
  } finally {
    // Restore button state
    syncText.textContent = originalText;
    syncBtn.disabled = false;
  }
}

// Pair with vault
async function pairWithVault() {
  const pairBtn = document.getElementById('pairBtn');
  const pairText = document.getElementById('pairText');
  const originalText = pairText.textContent;

  // Check if already paired
  const result = await chrome.storage.local.get(['authToken', 'extensionId']);
  if (result.authToken) {
    showError('Extension is already paired. Unpair first if you need to re-pair.');
    return;
  }

  // Prompt for pairing code
  const pairingCode = prompt('Enter the 6-digit pairing code from SecureVault:');
  if (!pairingCode) {
    return; // User cancelled
  }

  if (!/^\d{6}$/.test(pairingCode)) {
    showError('Please enter a valid 6-digit pairing code');
    return;
  }

  try {
    // Show loading state
    pairText.textContent = 'Pairing...';
    pairBtn.disabled = true;

    // Get or generate extension ID
    let extensionId = result.extensionId;
    if (!extensionId) {
      extensionId = generateExtensionId();
      await chrome.storage.local.set({ extensionId });
    }

    // Get vault URL
    const vaultResult = await chrome.storage.local.get(['vaultUrl']);
    const vaultUrl = vaultResult.vaultUrl || 'http://localhost:5000';

    // Send pairing request
    const response = await fetch(`${vaultUrl}/api/extension/pair`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        extensionId: extensionId,
        pairingCode: pairingCode,
        extensionName: 'SecureVault Browser Extension'
      })
    });

    const data = await response.json();

    if (response.ok && data.success) {
      // Store authentication token
      await chrome.storage.local.set({
        authToken: data.token,
        tokenExpiresAt: data.expiresAt,
        pairedAt: new Date().toISOString()
      });

      showSuccess('Successfully paired with SecureVault!');

      // Update connection status
      await checkVaultStatus();

    } else {
      throw new Error(data.error || 'Pairing failed');
    }

  } catch (error) {
    console.error('Pairing failed:', error);
    if (error.message.includes('Invalid pairing code')) {
      showError('Invalid or expired pairing code. Please generate a new one in SecureVault.');
    } else if (error.message.includes('already used')) {
      showError('This pairing code has already been used. Please generate a new one.');
    } else {
      showError('Pairing failed. Make sure SecureVault is running and try again.');
    }
  } finally {
    // Restore button state
    pairText.textContent = originalText;
    pairBtn.disabled = false;
  }
}

// Generate unique extension ID
function generateExtensionId() {
  return 'ext_' + Math.random().toString(36).substr(2, 9) + '_' + Date.now();
}

// Toggle auto-fill setting
async function toggleAutoFill() {
  const toggle = document.getElementById('autoFillToggle');
  const isEnabled = !toggle.classList.contains('active');

  updateToggle('autoFillToggle', isEnabled);

  // Save to storage
  await chrome.storage.local.set({
    autoFillEnabled: isEnabled
  });

  // Notify content scripts of change
  try {
    const [tab] = await chrome.tabs.query({ active: true, currentWindow: true });
    if (tab) {
      chrome.tabs.sendMessage(tab.id, {
        type: 'SETTING_CHANGED',
        setting: 'autoFillEnabled',
        value: isEnabled
      });
    }
  } catch (error) {
    // Content script may not be loaded, ignore
  }
}

// Toggle hints setting
async function toggleHints() {
  const toggle = document.getElementById('hintsToggle');
  const isEnabled = !toggle.classList.contains('active');

  updateToggle('hintsToggle', isEnabled);

  // Save to storage
  await chrome.storage.local.set({
    showHints: isEnabled
  });
}

// Update toggle visual state
function updateToggle(toggleId, isActive) {
  const toggle = document.getElementById(toggleId);
  if (isActive) {
    toggle.classList.add('active');
  } else {
    toggle.classList.remove('active');
  }
}

// Update last sync time display
async function updateLastSyncTime() {
  const result = await chrome.storage.local.get(['lastSync']);
  const lastSyncEl = document.getElementById('lastSync');

  if (result.lastSync) {
    const syncDate = new Date(result.lastSync);
    const now = new Date();
    const diffMinutes = Math.floor((now - syncDate) / (1000 * 60));

    let timeText;
    if (diffMinutes < 1) {
      timeText = 'Just now';
    } else if (diffMinutes < 60) {
      timeText = `${diffMinutes} minute${diffMinutes === 1 ? '' : 's'} ago`;
    } else if (diffMinutes < 1440) {
      const hours = Math.floor(diffMinutes / 60);
      timeText = `${hours} hour${hours === 1 ? '' : 's'} ago`;
    } else {
      timeText = syncDate.toLocaleDateString();
    }

    lastSyncEl.textContent = `Last sync: ${timeText}`;
  } else {
    lastSyncEl.textContent = 'Last sync: Never';
  }
}

// Send message to background script
function sendMessage(message) {
  return new Promise((resolve, reject) => {
    chrome.runtime.sendMessage(message, (response) => {
      if (chrome.runtime.lastError) {
        reject(new Error(chrome.runtime.lastError.message));
      } else {
        resolve(response);
      }
    });
  });
}

// Show success message
function showSuccess(message) {
  showNotification(message, 'success');
}

// Show error message
function showError(message) {
  showNotification(message, 'error');
}

// Show notification (simple implementation for popup)
function showNotification(message, type) {
  // For popup, we could create a temporary notification element
  // or just use console for now since popup closes quickly
  console.log(`${type.toUpperCase()}: ${message}`);

  // Could also briefly change button colors or show status
  if (type === 'success') {
    // Briefly flash green
    document.body.style.borderTop = '3px solid #10B981';
    setTimeout(() => {
      document.body.style.borderTop = 'none';
    }, 2000);
  } else if (type === 'error') {
    // Briefly flash red
    document.body.style.borderTop = '3px solid #EF4444';
    setTimeout(() => {
      document.body.style.borderTop = 'none';
    }, 2000);
  }
}

