import { useState, useMemo } from 'react';
import { useVault } from '@/contexts/vault-context';
import { NoteEntry, NOTE_NOTEBOOKS } from '@shared/schema';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Search, 
  BookOpen, 
  Tag, 
  Pin, 
  Calendar,
  Filter,
  StickyNote,
  Archive,
  Eye,
  FileText
} from 'lucide-react';
import { format } from 'date-fns';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import rehypeHighlight from 'rehype-highlight';
import 'highlight.js/styles/github-dark.css';

export default function Notes() {
  const { notes, addNote, updateNote, deleteNote, searchQuery, setSearchQuery } = useVault();
  const { toast } = useToast();
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingNote, setEditingNote] = useState<NoteEntry | null>(null);
  const [selectedNotebook, setSelectedNotebook] = useState<string>('all');
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [showPinnedOnly, setShowPinnedOnly] = useState(false);

  // Form state for add/edit modal
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    notebook: 'Default',
    tags: [] as string[],
    isPinned: false,
  });

  const [newTag, setNewTag] = useState('');

  // Get all unique tags from notes
  const allTags = useMemo(() => {
    const tagSet = new Set<string>();
    notes.forEach(note => {
      note.tags.forEach(tag => tagSet.add(tag));
    });
    return Array.from(tagSet).sort();
  }, [notes]);

  // Filter notes based on search, notebook, tags, and pinned status
  const filteredNotes = useMemo(() => {
    return notes.filter(note => {
      // Search filter
      const matchesSearch = !searchQuery || 
        note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        note.content.toLowerCase().includes(searchQuery.toLowerCase()) ||
        note.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));

      // Notebook filter
      const matchesNotebook = selectedNotebook === 'all' || note.notebook === selectedNotebook;

      // Tag filter
      const matchesTags = selectedTags.length === 0 || 
        selectedTags.some(tag => note.tags.includes(tag));

      // Pinned filter
      const matchesPinned = !showPinnedOnly || note.isPinned;

      return matchesSearch && matchesNotebook && matchesTags && matchesPinned;
    });
  }, [notes, searchQuery, selectedNotebook, selectedTags, showPinnedOnly]);

  // Sort notes: pinned first, then by updated date
  const sortedNotes = useMemo(() => {
    return [...filteredNotes].sort((a, b) => {
      if (a.isPinned && !b.isPinned) return -1;
      if (!a.isPinned && b.isPinned) return 1;
      return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
    });
  }, [filteredNotes]);

  const handleAddNote = async () => {
    if (!formData.title.trim()) {
      toast({
        title: "Error",
        description: "Title is required",
        variant: "destructive",
      });
      return;
    }

    try {
      await addNote({
        title: formData.title.trim(),
        content: formData.content,
        notebook: formData.notebook,
        tags: formData.tags,
        isPinned: formData.isPinned,
      });

      setFormData({
        title: '',
        content: '',
        notebook: 'Default',
        tags: [],
        isPinned: false,
      });
      setShowAddModal(false);

      toast({
        title: "Success",
        description: "Note added successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add note",
        variant: "destructive",
      });
    }
  };

  const handleEditNote = (note: NoteEntry) => {
    setEditingNote(note);
    setFormData({
      title: note.title,
      content: note.content,
      notebook: note.notebook,
      tags: [...note.tags],
      isPinned: note.isPinned,
    });
  };

  const handleUpdateNote = async () => {
    if (!editingNote || !formData.title.trim()) {
      toast({
        title: "Error", 
        description: "Title is required",
        variant: "destructive",
      });
      return;
    }

    try {
      await updateNote(editingNote.id, {
        title: formData.title.trim(),
        content: formData.content,
        notebook: formData.notebook,
        tags: formData.tags,
        isPinned: formData.isPinned,
      });

      setEditingNote(null);
      setFormData({
        title: '',
        content: '',
        notebook: 'Default',
        tags: [],
        isPinned: false,
      });

      toast({
        title: "Success",
        description: "Note updated successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update note",
        variant: "destructive",
      });
    }
  };

  const handleDeleteNote = async (id: string, title: string) => {
    if (!confirm(`Are you sure you want to delete "${title}"?`)) {
      return;
    }

    try {
      await deleteNote(id);
      toast({
        title: "Success",
        description: "Note deleted successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete note",
        variant: "destructive",
      });
    }
  };

  const togglePinNote = async (note: NoteEntry) => {
    try {
      await updateNote(note.id, { isPinned: !note.isPinned });
      toast({
        title: "Success",
        description: note.isPinned ? "Note unpinned" : "Note pinned",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update note",
        variant: "destructive",
      });
    }
  };

  const addTag = () => {
    if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
      setFormData(prev => ({
        ...prev,
        tags: [...prev.tags, newTag.trim()]
      }));
      setNewTag('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setFormData(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
  };

  const toggleTagFilter = (tag: string) => {
    setSelectedTags(prev => 
      prev.includes(tag) 
        ? prev.filter(t => t !== tag)
        : [...prev, tag]
    );
  };

  const clearFilters = () => {
    setSelectedNotebook('all');
    setSelectedTags([]);
    setShowPinnedOnly(false);
    setSearchQuery('');
  };

  const renderNoteModal = () => (
    <Dialog open={showAddModal || !!editingNote} onOpenChange={(open) => {
      if (!open) {
        setShowAddModal(false);
        setEditingNote(null);
        setFormData({
          title: '',
          content: '',
          notebook: 'Default',
          tags: [],
          isPinned: false,
        });
        setNewTag('');
      }
    }}>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <StickyNote className="w-5 h-5" />
            {editingNote ? 'Edit Note' : 'Add New Note'}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Title */}
          <div className="space-y-2">
            <Label htmlFor="title">Title *</Label>
            <Input
              id="title"
              data-testid="input-note-title"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Enter note title..."
              className="text-lg"
            />
          </div>

          {/* Notebook and Pin */}
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="notebook">Notebook</Label>
              <Select 
                value={formData.notebook} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, notebook: value }))}
              >
                <SelectTrigger data-testid="select-note-notebook">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {NOTE_NOTEBOOKS.map(notebook => (
                    <SelectItem key={notebook} value={notebook}>{notebook}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  data-testid="checkbox-note-pinned"
                  checked={formData.isPinned}
                  onChange={(e) => setFormData(prev => ({ ...prev, isPinned: e.target.checked }))}
                  className="rounded"
                />
                <Pin className="w-4 h-4" />
                Pin this note
              </Label>
            </div>
          </div>

          {/* Tags */}
          <div className="space-y-2">
            <Label>Tags</Label>
            <div className="flex gap-2">
              <Input
                data-testid="input-note-tag"
                value={newTag}
                onChange={(e) => setNewTag(e.target.value)}
                placeholder="Add a tag..."
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    addTag();
                  }
                }}
                className="flex-1"
              />
              <Button 
                type="button"
                onClick={addTag}
                data-testid="button-add-tag"
                variant="outline"
                size="sm"
              >
                <Tag className="w-4 h-4 mr-1" />
                Add
              </Button>
            </div>
            {formData.tags.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {formData.tags.map(tag => (
                  <Badge 
                    key={tag} 
                    variant="secondary" 
                    className="cursor-pointer"
                    onClick={() => removeTag(tag)}
                    data-testid={`badge-tag-${tag}`}
                  >
                    {tag} Ã—
                  </Badge>
                ))}
              </div>
            )}
          </div>

          {/* Content with Markdown Editor */}
          <div className="space-y-2">
            <Label>Content</Label>
            <Tabs defaultValue="edit" className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="edit" className="flex items-center gap-2" data-testid="tab-edit">
                  <FileText className="w-4 h-4" />
                  Edit
                </TabsTrigger>
                <TabsTrigger value="preview" className="flex items-center gap-2" data-testid="tab-preview">
                  <Eye className="w-4 h-4" />
                  Preview
                </TabsTrigger>
              </TabsList>

              <TabsContent value="edit" className="space-y-2">
                <Textarea
                  data-testid="textarea-note-content"
                  value={formData.content}
                  onChange={(e) => setFormData(prev => ({ ...prev, content: e.target.value }))}
                  placeholder="Write your note content here using Markdown...

# Heading 1
## Heading 2

**Bold text** and *italic text*

- Bullet point
- Another item

1. Numbered list
2. Second item

`inline code`

```javascript
// Code block
console.log('Hello, world!');
```

[Link](https://example.com)

> Blockquote

---

**Tables:**
| Column 1 | Column 2 |
|----------|----------|
| Data 1   | Data 2   |
"
                  className="min-h-64 font-mono text-sm resize-none"
                />
                <div className="text-xs text-muted-foreground">
                  <strong>Markdown Support:</strong> Headers (#), **bold**, *italic*, `code`, lists (-, 1.), 
                  [links](url), &gt; quotes, tables, code blocks ```lang, and more
                </div>
              </TabsContent>

              <TabsContent value="preview" className="space-y-2">
                <div 
                  className="min-h-64 p-4 border rounded-md bg-background prose prose-sm dark:prose-invert max-w-none"
                  data-testid="markdown-preview"
                >
                  {formData.content ? (
                    <ReactMarkdown 
                      remarkPlugins={[remarkGfm]}
                      rehypePlugins={[rehypeHighlight]}
                    >
                      {formData.content}
                    </ReactMarkdown>
                  ) : (
                    <div className="text-muted-foreground italic">
                      Nothing to preview. Write some content in the Edit tab.
                    </div>
                  )}
                </div>
              </TabsContent>
            </Tabs>
          </div>

          {/* Actions */}
          <div className="flex justify-end gap-2">
            <Button
              type="button"
              variant="outline"
              onClick={() => {
                setShowAddModal(false);
                setEditingNote(null);
              }}
            >
              Cancel
            </Button>
            <Button
              type="button"
              data-testid="button-save-note"
              onClick={editingNote ? handleUpdateNote : handleAddNote}
            >
              {editingNote ? 'Update Note' : 'Add Note'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold flex items-center gap-2">
            <BookOpen className="w-6 h-6" />
            Notes
          </h1>
          <p className="text-muted-foreground">
            Organize your thoughts with encrypted notes
          </p>
        </div>
        <Button onClick={() => setShowAddModal(true)} data-testid="button-add-note">
          <Plus className="w-4 h-4 mr-2" />
          Add Note
        </Button>
      </div>

      {/* Search and Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base flex items-center gap-2">
            <Filter className="w-4 h-4" />
            Search & Filters
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <Input
              data-testid="input-notes-search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search notes by title, content, or tags..."
              className="pl-10"
            />
          </div>

          {/* Filter Controls */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Notebook Filter */}
            <div className="space-y-2">
              <Label>Notebook</Label>
              <Select value={selectedNotebook} onValueChange={setSelectedNotebook}>
                <SelectTrigger data-testid="select-notebook-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Notebooks</SelectItem>
                  {NOTE_NOTEBOOKS.map(notebook => (
                    <SelectItem key={notebook} value={notebook}>{notebook}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Quick Filters */}
            <div className="space-y-2">
              <Label>Quick Filters</Label>
              <div className="flex gap-2">
                <Button
                  variant={showPinnedOnly ? "default" : "outline"}
                  size="sm"
                  data-testid="button-filter-pinned"
                  onClick={() => setShowPinnedOnly(!showPinnedOnly)}
                >
                  <Pin className="w-4 h-4 mr-1" />
                  Pinned
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  data-testid="button-clear-filters"
                  onClick={clearFilters}
                >
                  Clear All
                </Button>
              </div>
            </div>

            {/* Stats */}
            <div className="space-y-2">
              <Label>Notes</Label>
              <div className="text-sm text-muted-foreground">
                Showing {sortedNotes.length} of {notes.length} notes
              </div>
            </div>
          </div>

          {/* Tag Filters */}
          {allTags.length > 0 && (
            <div className="space-y-2">
              <Label>Filter by Tags</Label>
              <div className="flex flex-wrap gap-2">
                {allTags.map(tag => (
                  <Badge
                    key={tag}
                    variant={selectedTags.includes(tag) ? "default" : "outline"}
                    className="cursor-pointer"
                    onClick={() => toggleTagFilter(tag)}
                    data-testid={`filter-tag-${tag}`}
                  >
                    {tag}
                  </Badge>
                ))}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Notes Grid */}
      {sortedNotes.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Archive className="w-12 h-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium mb-2">No Notes Found</h3>
            <p className="text-muted-foreground text-center mb-4">
              {notes.length === 0 
                ? "Get started by creating your first note"
                : "Try adjusting your search or filter criteria"
              }
            </p>
            {notes.length === 0 && (
              <Button onClick={() => setShowAddModal(true)} data-testid="button-create-first-note">
                <Plus className="w-4 h-4 mr-2" />
                Create Your First Note
              </Button>
            )}
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {sortedNotes.map(note => (
            <Card key={note.id} className={`cursor-pointer transition-shadow hover:shadow-md ${note.isPinned ? 'ring-2 ring-amber-200' : ''}`}>
              <CardHeader className="pb-2">
                <div className="flex items-start justify-between">
                  <CardTitle className="text-base line-clamp-2 flex items-center gap-2">
                    {note.isPinned && <Pin className="w-4 h-4 text-amber-500 flex-shrink-0" />}
                    <span data-testid={`note-title-${note.id}`}>{note.title}</span>
                  </CardTitle>
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      data-testid={`button-pin-${note.id}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        togglePinNote(note);
                      }}
                      className="p-1 h-auto"
                    >
                      <Pin className={`w-3 h-3 ${note.isPinned ? 'text-amber-500' : 'text-muted-foreground'}`} />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      data-testid={`button-edit-${note.id}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEditNote(note);
                      }}
                      className="p-1 h-auto"
                    >
                      <Edit className="w-3 h-3" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      data-testid={`button-delete-${note.id}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteNote(note.id, note.title);
                      }}
                      className="p-1 h-auto text-destructive hover:text-destructive"
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  </div>
                </div>

                <div className="flex items-center gap-2 text-xs text-muted-foreground">
                  <BookOpen className="w-3 h-3" />
                  {note.notebook}
                  <Calendar className="w-3 h-3 ml-2" />
                  {format(new Date(note.updatedAt), 'MMM d, yyyy')}
                </div>
              </CardHeader>

              <CardContent className="pt-2">
                {note.content && (
                  <p className="text-sm text-muted-foreground line-clamp-4 mb-3" data-testid={`note-content-${note.id}`}>
                    {note.content}
                  </p>
                )}

                {note.tags.length > 0 && (
                  <div className="flex flex-wrap gap-1">
                    {note.tags.slice(0, 3).map(tag => (
                      <Badge key={tag} variant="secondary" className="text-xs" data-testid={`note-tag-${note.id}-${tag}`}>
                        {tag}
                      </Badge>
                    ))}
                    {note.tags.length > 3 && (
                      <Badge variant="outline" className="text-xs">
                        +{note.tags.length - 3} more
                      </Badge>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {renderNoteModal()}
    </div>
  );
}

